contents.content:
struct C.timeval {
	tv_sec  u64
	tv_usec u64
}

struct NotExpected {
	cause string
	code  int
}

struct FileInfo {
	FileMode
pub:
	size  u64 // size of the file in bytes
	mtime i64 // last modification time in seconds after the Unix epoch
}

struct FilePermission {
pub:
	read    bool
	write   bool
	execute bool
}

struct Result {
pub:
	exit_code int
	output    string
	// stderr string // TODO
}

struct MkdirParams {
	mode u32 = 0o777 // note that the actual mode is affected by the process's umask
}

struct C._utimbuf {
	actime  int
	modtime int
}

struct ExceptionRecord {
pub:
	// status_ constants
	code        u32
	flags       u32
	record      &ExceptionRecord = unsafe { nil }
	address     voidptr
	param_count u32
	// params []voidptr
}

struct ExceptionPointers {
pub:
	exception_record &ExceptionRecord = unsafe { nil }
	context_record   &ContextRecord   = unsafe { nil }
}

struct C.SYSTEM_INFO {
	dwNumberOfProcessors u32
	dwPageSize           u32
}

struct WProcess {
pub mut:
	proc_info    ProcessInformation
	command_line [65536]u8
	child_stdin  &u32 = unsafe { nil }
	//
	child_stdout_read  &u32 = unsafe { nil }
	child_stdout_write &u32 = unsafe { nil }
	//
	child_stderr_read  &u32 = unsafe { nil }
	child_stderr_write &u32 = unsafe { nil }
}

struct C.stat {
	st_size  u64
	st_mode  u32
	st_mtime int
}

struct C.__stat64 {
	st_size  u64
	st_mode  u32
	st_mtime int
}

struct C.utimbuf {
	actime  int
	modtime int
}
